import React, { useState, useEffect } from 'react';
import { Folder, File, ChevronLeft, Download, Loader2, AlertCircle } from 'lucide-react';
import { formatFileSize, formatDate } from '../../utils/formatters';
import apiService from '../../services/api';
import FileUpload from '../FileUpload/FileUpload';
import DownloadProgress from '../DownloadProgress/DownloadProgress';
import './FileExplorer.css';
import { FiSearch, FiFilter, FiLock, FiGrid, FiList } from 'react-icons/fi';

const FileExplorer = ({ onFileSelect }) => {
  const [currentPath, setCurrentPath] = useState('');
  const [files, setFiles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [breadcrumbs, setBreadcrumbs] = useState([]);
  const [uploadSuccess, setUploadSuccess] = useState(null);
  const [uploadError, setUploadError] = useState(null);
  const [downloadSessions, setDownloadSessions] = useState([]);
  const [showDownloadProgress, setShowDownloadProgress] = useState(false);
  const [clientId, setClientId] = useState(null);

  // Get client ID for downloads
  useEffect(() => {
    const getClientId = async () => {
      try {
        const response = await apiService.connect();
        setClientId(response.client_id);
        console.log('Client connected:', response.client_id);
      } catch (error) {
        console.error('Failed to get client ID:', error);
        setError(`Connection failed: ${error.message}`);
      }
    };
    getClientId();
  }, []);

  useEffect(() => {
    fetchFiles(currentPath);
  }, [currentPath]);

  const fetchFiles = async (path) => {
    try {
      setIsLoading(true);
      setError(null);
      
      const data = await apiService.listFiles(path);
      setFiles(data.files);
      
      // Generate breadcrumbs
      const pathParts = path.split('/').filter(Boolean);
      const crumbs = [];
      let accumulatedPath = '';
      
      for (let i = 0; i < pathParts.length; i++) {
        accumulatedPath += (i > 0 ? '/' : '') + pathParts[i];
        crumbs.push({
          name: pathParts[i],
          path: accumulatedPath
        });
      }
      
      setBreadcrumbs(crumbs);
    } catch (err) {
      console.error('Failed to fetch files:', err);
      setError(err.message);
      setFiles([]);
    } finally {
      setIsLoading(false);
    }
  };

  const navigateToDirectory = (path) => {
    setCurrentPath(path);
  };

  const navigateUp = () => {
    if (!currentPath) return;
    
    const pathParts = currentPath.split('/');
    pathParts.pop();
    setCurrentPath(pathParts.join('/'));
  };

  const handleFileClick = (file) => {
    if (file.is_directory) {
      navigateToDirectory(file.path);
    } else {
      if (onFileSelect) {
        onFileSelect(file);
      }
    }
  };

  const startMegaStyleDownload = async (filePath, fileName, clientId) => {
    try {
      // Start download session using API service
      const sessionData = await apiService.startDownloadSession(filePath, clientId);
      
      // Add to downloads list
      const newDownload = {
        sessionId: sessionData.session_id,
        fileName: sessionData.file_name,
        fileSize: sessionData.file_size,
        clientId: sessionData.client_id,
        progress: 0,
        speed: 0,
        eta: 0,
        status: 'starting',
        startTime: Date.now()
      };

      setDownloadSessions(prev => [...prev, newDownload]);

      // Immediately trigger browser download (no waiting for processing)
      triggerBrowserDownload(newDownload.sessionId);
      
      // Start progress tracking
      trackDownloadProgress(newDownload.sessionId);

    } catch (error) {
      console.error('Download error:', error);
      throw error;
    }
  };

  const trackDownloadProgress = async (sessionId) => {
    const pollProgress = async () => {
      try {
        const progress = await apiService.getDownloadProgress(sessionId);
        
        setDownloadSessions(prev => prev.map(download => 
          download.sessionId === sessionId 
            ? {
              ...download,
              progress: progress.progress_percent,
              speed: progress.speed_mbps,
              eta: progress.eta_seconds,
              bytesTransferred: progress.bytes_transferred,
              status: progress.is_complete ? 'completed' : 
                     progress.is_processing ? 'downloading' : 'starting'
            }
            : download
        ));

        // Continue polling while download is active
        if (!progress.error && !progress.is_complete) {
          setTimeout(pollProgress, 500);  // Poll every 500ms for smoother updates
        } else if (progress.is_complete) {
          // Mark as completed - no need to trigger another download
          setDownloadSessions(prev => prev.map(download => 
            download.sessionId === sessionId 
              ? { ...download, status: 'completed' }
              : download
          ));
        }

      } catch (error) {
        console.error('Progress tracking error:', error);
        setDownloadSessions(prev => prev.map(download => 
          download.sessionId === sessionId 
            ? { ...download, status: 'error', error: error.message }
            : download
        ));
      }
    };

    pollProgress();
  };

  const triggerBrowserDownload = (sessionId) => {
    // Update status to show download is starting
    setDownloadSessions(prev => prev.map(download => 
      download.sessionId === sessionId 
        ? { ...download, status: 'downloading' }
        : download
    ));

    // Create hidden link to trigger download - use full URL from API service
    const link = document.createElement('a');
    const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    link.href = `${baseURL}/transfer/stream-download/${sessionId}`;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();

    // Clean up
    setTimeout(() => {
      document.body.removeChild(link);
    }, 100);
  };

  const handleDownload = async (file, e) => {
    e.stopPropagation();
    
    // Try to reconnect if no client ID
    if (!clientId) {
      try {
        console.log('No client ID, attempting to reconnect...');
        const response = await apiService.connect();
        setClientId(response.client_id);
        console.log('Reconnected with client ID:', response.client_id);
      } catch (error) {
        console.error('Reconnection failed:', error);
        setError(`Connection failed: ${error.message}`);
        return;
      }
    }

    try {
      // Show download progress panel
      setShowDownloadProgress(true);
      
      // Start the Mega.nz style download directly
      await startMegaStyleDownload(file.path, file.name, clientId);
    } catch (err) {
      console.error('Download failed:', err);
      setError(err.message);
    }
  };

  const handleUploadSuccess = (response) => {
    setUploadSuccess(`Successfully uploaded ${response.filename}`);
    setUploadError(null);
    fetchFiles(currentPath); // Refresh file list
    
    // Clear success message after 5 seconds
    setTimeout(() => {
      setUploadSuccess(null);
    }, 5000);
  };

  const handleUploadError = (error) => {
    setUploadError(error);
    setUploadSuccess(null);
    
    // Clear error message after 7 seconds
    setTimeout(() => {
      setUploadError(null);
    }, 7000);
  };

  if (error) {
    return (
      <div className="file-explorer error">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <h3 className="text-xl font-semibold mb-2">Error Loading Files</h3>
        <p className="text-gray-600 mb-4">{error}</p>
        <button 
          onClick={() => fetchFiles(currentPath)}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          Retry
        </button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="file-explorer loading">
        <Loader2 size={48} className="animate-spin text-blue-500 mb-4" />
        <p>Loading files...</p>
      </div>
    );
  }

  return (
    <div className="file-explorer">
       <div className="file-manager-header">
            <div className="left-section">
              <h2 className="header-title">File Manager</h2>
              <div className="search-filter-group">
                <div className="search-box">
                  <FiSearch className="search-icon" />
                  <input type="text" placeholder="Search files…" />
                </div>
                <button className="filter-button">
                  <FiFilter className="filter-icon" /> Filter
                </button>
              </div>
            </div>
      
            <div className="right-section">
              <div className="storage-indicator">
                <FiLock className="lock-icon" />
                <span>2.3 GB / 10 GB</span>
              </div>
              <button className="upload-button">+ Upload</button>
              <div className="view-toggle">
                <button className="view-btn selected"><FiGrid /></button>
                <button className="view-btn"><FiList /></button>
              </div>
            </div>
          </div>
          
      {files.length === 0 ? (
        <div className="empty-directory">
          <Folder size={48} className="mx-auto mb-4 text-gray-400" />
          <h3 className="text-lg font-medium text-gray-600">Empty directory</h3>
          <p className="text-gray-500">No files found in this location</p>
        </div>
      ) : (
        <div className="file-grid">
          {files.map((file) => (
            <div
              key={file.path}
              onClick={() => handleFileClick(file)}
              className={`file-item ${file.is_directory ? 'directory' : ''}`}
            >
              <div className="file-icon">
                {file.is_directory ? (
                  <Folder size={24} />
                ) : (
                  <File size={24} />
                )}
              </div>
              
              <div className="file-info">
                <div className="file-name">{file.name}</div>
                <div className="file-details">
                  <span>{formatFileSize(file.size)}</span>
                  <span>{formatDate(file.modified_time)}</span>
                </div>
              </div>
              
              {!file.is_directory && (
                <button
                  onClick={(e) => handleDownload(file, e)}
                  className="download-button modern-download"
                  title={`Download ${file.name} with real-time metrics`}
                >
                  <Download size={16} />
                  <span className="download-text">Download</span>
                </button>
              )}
            </div>
          ))}
        </div>
      )}

      <FileUpload 
        currentPath={currentPath}
        onUploadSuccess={handleUploadSuccess}
        onUploadError={handleUploadError}
      />
      
      {uploadSuccess && (
        <div className="upload-feedback success">
          {uploadSuccess}
        </div>
      )}
      
      {uploadError && (
        <div className="upload-feedback error">
          {uploadError}
        </div>
      )}
      
      {showDownloadProgress && downloadSessions.length > 0 && (
        <DownloadProgress 
          downloads={downloadSessions}
          onClose={() => setShowDownloadProgress(false)}
          onRemoveDownload={(sessionId) => 
            setDownloadSessions(prev => prev.filter(d => d.sessionId !== sessionId))
          }
        />
      )}
    </div>
  );
};

export default FileExplorer;
