import React, { useState, useEffect } from 'react';
import { 
  Folder, 
  File, 
  Search, 
  Grid3X3, 
  List, 
  MoreVertical,
  Download,
  Star,
  Share2,
  Trash2,
  Info,
  FolderOpen,
  FileText,
  Image,
  Video,
  Music,
  Archive,
  Code,
  Loader2,
  SortAsc,
  SortDesc,
  Filter,
  X
} from 'lucide-react';

const GoogleDriveFiles = ({ onFileSelect }) => {
  const [files, setFiles] = useState([]);
  const [filteredFiles, setFilteredFiles] = useState([]);
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const [selectedFiles, setSelectedFiles] = useState(new Set());
  const [showSortMenu, setShowSortMenu] = useState(false);
  const [showFilterMenu, setShowFilterMenu] = useState(false);
  const [filterType, setFilterType] = useState('all');
  const [isLoading, setIsLoading] = useState(true);

  // Mock data - replace with your API calls
  const mockFiles = [
    {
      id: '1',
      name: 'Project Documents',
      type: 'folder',
      size: 0,
      modified: '2024-01-15T10:30:00Z',
      owner: 'me',
      shared: false,
      starred: false,
      path: '/project-docs'
    },
    {
      id: '2',
      name: 'presentation.pptx',
      type: 'presentation',
      size: 2048000,
      modified: '2024-01-14T15:45:00Z',
      owner: 'me',
      shared: true,
      starred: true,
      path: '/presentation.pptx'
    },
    {
      id: '3',
      name: 'vacation-photos.zip',
      type: 'archive',
      size: 52428800,
      modified: '2024-01-13T09:20:00Z',
      owner: 'me',
      shared: false,
      starred: false,
      path: '/vacation-photos.zip'
    },
    {
      id: '4',
      name: 'budget-2024.xlsx',
      type: 'spreadsheet',
      size: 102400,
      modified: '2024-01-12T14:15:00Z',
      owner: 'me',
      shared: false,
      starred: false,
      path: '/budget-2024.xlsx'
    },
    {
      id: '5',
      name: 'meeting-notes.docx',
      type: 'document',
      size: 51200,
      modified: '2024-01-11T11:00:00Z',
      owner: 'me',
      shared: true,
      starred: false,
      path: '/meeting-notes.docx'
    },
    {
      id: '6',
      name: 'profile-photo.jpg',
      type: 'image',
      size: 1024000,
      modified: '2024-01-10T16:30:00Z',
      owner: 'me',
      shared: false,
      starred: true,
      path: '/profile-photo.jpg'
    },
    {
      id: '7',
      name: 'demo-video.mp4',
      type: 'video',
      size: 15728640,
      modified: '2024-01-09T13:45:00Z',
      owner: 'me',
      shared: false,
      starred: false,
      path: '/demo-video.mp4'
    },
    {
      id: '8',
      name: 'src',
      type: 'folder',
      size: 0,
      modified: '2024-01-08T10:00:00Z',
      owner: 'me',
      shared: false,
      starred: false,
      path: '/src'
    }
  ];

  useEffect(() => {
    // Simulate API call
    const loadFiles = async () => {
      setIsLoading(true);
      await new Promise(resolve => setTimeout(resolve, 800));
      setFiles(mockFiles);
      setIsLoading(false);
    };
    loadFiles();
  }, []);

  useEffect(() => {
    let filtered = files;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(file => 
        file.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by type
    if (filterType !== 'all') {
      filtered = filtered.filter(file => {
        switch (filterType) {
          case 'folders':
            return file.type === 'folder';
          case 'documents':
            return ['document', 'spreadsheet', 'presentation'].includes(file.type);
          case 'images':
            return file.type === 'image';
          case 'videos':
            return file.type === 'video';
          case 'shared':
            return file.shared;
          case 'starred':
            return file.starred;
          default:
            return true;
        }
      });
    }

    // Sort files
    filtered = filtered.sort((a, b) => {
      let aVal, bVal;
      
      switch (sortBy) {
        case 'name':
          aVal = a.name.toLowerCase();
          bVal = b.name.toLowerCase();
          break;
        case 'modified':
          aVal = new Date(a.modified);
          bVal = new Date(b.modified);
          break;
        case 'size':
          aVal = a.size;
          bVal = b.size;
          break;
        case 'type':
          aVal = a.type;
          bVal = b.type;
          break;
        default:
          aVal = a.name.toLowerCase();
          bVal = b.name.toLowerCase();
      }

      if (sortOrder === 'asc') {
        return aVal > bVal ? 1 : -1;
      } else {
        return aVal < bVal ? 1 : -1;
      }
    });

    setFilteredFiles(filtered);
  }, [files, searchTerm, sortBy, sortOrder, filterType]);

  const getFileIcon = (type) => {
    switch (type) {
      case 'folder':
        return <Folder className="text-blue-500" size={20} />;
      case 'document':
        return <FileText className="text-blue-600" size={20} />;
      case 'spreadsheet':
        return <FileText className="text-green-600" size={20} />;
      case 'presentation':
        return <FileText className="text-orange-600" size={20} />;
      case 'image':
        return <Image className="text-purple-600" size={20} />;
      case 'video':
        return <Video className="text-red-600" size={20} />;
      case 'audio':
        return <Music className="text-pink-600" size={20} />;
      case 'archive':
        return <Archive className="text-gray-600" size={20} />;
      case 'code':
        return <Code className="text-green-700" size={20} />;
      default:
        return <File className="text-gray-500" size={20} />;
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffDays = Math.floor((now - date) / (1000 * 60 * 60 * 24));
    
    if (diffDays === 0) return 'Today';
    if (diffDays === 1) return 'Yesterday';
    if (diffDays < 7) return `${diffDays} days ago`;
    return date.toLocaleDateString();
  };

  const handleFileClick = (file) => {
    if (file.type === 'folder') {
      // Navigate to folder
      console.log('Opening folder:', file.path);
    } else {
      if (onFileSelect) {
        onFileSelect(file);
      }
    }
  };

  const handleFileSelect = (fileId, event) => {
    event.stopPropagation();
    const newSelected = new Set(selectedFiles);
    if (newSelected.has(fileId)) {
      newSelected.delete(fileId);
    } else {
      newSelected.add(fileId);
    }
    setSelectedFiles(newSelected);
  };

  const toggleStar = (fileId, event) => {
    event.stopPropagation();
    setFiles(prev => prev.map(file => 
      file.id === fileId ? { ...file, starred: !file.starred } : file
    ));
  };

  const clearFilters = () => {
    setSearchTerm('');
    setFilterType('all');
    setSortBy('name');
    setSortOrder('asc');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 size={32} className="animate-spin text-blue-500 mr-3" />
        <span className="text-gray-600">Loading files...</span>
      </div>
    );
  }

  return (
    <div className="w-full max-w-6xl mx-auto p-4 bg-white">
      {/* Header */}
      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-normal text-gray-800">All Files</h1>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded-lg ${viewMode === 'grid' ? 'bg-blue-100 text-blue-600' : 'text-gray-600 hover:bg-gray-100'}`}
            >
              <Grid3X3 size={20} />
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded-lg ${viewMode === 'list' ? 'bg-blue-100 text-blue-600' : 'text-gray-600 hover:bg-gray-100'}`}
            >
              <List size={20} />
            </button>
          </div>
        </div>

        {/* Search and Filter Bar */}
        <div className="flex items-center space-x-3 mb-4">
          <div className="relative flex-1 max-w-lg">
            <Search size={20} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search in Drive"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
          
          <div className="relative">
            <button
              onClick={() => setShowSortMenu(!showSortMenu)}
              className="flex items-center space-x-2 px-3 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              {sortOrder === 'asc' ? <SortAsc size={16} /> : <SortDesc size={16} />}
              <span className="text-sm capitalize">{sortBy}</span>
            </button>
            
            {showSortMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                <div className="py-1">
                  {['name', 'modified', 'size', 'type'].map((option) => (
                    <button
                      key={option}
                      onClick={() => {
                        setSortBy(option);
                        setShowSortMenu(false);
                      }}
                      className={`w-full text-left px-4 py-2 text-sm hover:bg-gray-100 ${sortBy === option ? 'bg-blue-50 text-blue-600' : 'text-gray-700'}`}
                    >
                      {option.charAt(0).toUpperCase() + option.slice(1)}
                    </button>
                  ))}
                  <hr className="my-1" />
                  <button
                    onClick={() => {
                      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
                      setShowSortMenu(false);
                    }}
                    className="w-full text-left px-4 py-2 text-sm hover:bg-gray-100 text-gray-700"
                  >
                    {sortOrder === 'asc' ? 'Descending' : 'Ascending'}
                  </button>
                </div>
              </div>
            )}
          </div>

          <div className="relative">
            <button
              onClick={() => setShowFilterMenu(!showFilterMenu)}
              className="flex items-center space-x-2 px-3 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              <Filter size={16} />
              <span className="text-sm">Filter</span>
            </button>
            
            {showFilterMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 z-10">
                <div className="py-1">
                  {[
                    { value: 'all', label: 'All files' },
                    { value: 'folders', label: 'Folders' },
                    { value: 'documents', label: 'Documents' },
                    { value: 'images', label: 'Images' },
                    { value: 'videos', label: 'Videos' },
                    { value: 'shared', label: 'Shared' },
                    { value: 'starred', label: 'Starred' }
                  ].map((option) => (
                    <button
                      key={option.value}
                      onClick={() => {
                        setFilterType(option.value);
                        setShowFilterMenu(false);
                      }}
                      className={`w-full text-left px-4 py-2 text-sm hover:bg-gray-100 ${filterType === option.value ? 'bg-blue-50 text-blue-600' : 'text-gray-700'}`}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          {(searchTerm || filterType !== 'all' || sortBy !== 'name') && (
            <button
              onClick={clearFilters}
              className="flex items-center space-x-1 px-3 py-2 text-sm text-gray-600 hover:bg-gray-100 rounded-lg"
            >
              <X size={16} />
              <span>Clear</span>
            </button>
          )}
        </div>
      </div>

      {/* Files Display */}
      {filteredFiles.length === 0 ? (
        <div className="text-center py-12">
          <FolderOpen size={48} className="mx-auto text-gray-400 mb-4" />
          <h3 className="text-lg font-medium text-gray-600 mb-2">No files found</h3>
          <p className="text-gray-500">Try adjusting your search or filter criteria</p>
        </div>
      ) : (
        <div className={viewMode === 'grid' ? 'grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4' : 'space-y-1'}>
          {filteredFiles.map((file) => (
            <div key={file.id}>
              {viewMode === 'grid' ? (
                <div
                  onClick={() => handleFileClick(file)}
                  className={`p-4 rounded-lg border-2 cursor-pointer transition-all hover:shadow-md ${
                    selectedFiles.has(file.id) 
                      ? 'border-blue-500 bg-blue-50' 
                      : 'border-gray-200 hover:border-gray-300'
                  }`}
                >
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-2">
                      {getFileIcon(file.type)}
                      <input
                        type="checkbox"
                        checked={selectedFiles.has(file.id)}
                        onChange={(e) => handleFileSelect(file.id, e)}
                        className="w-4 h-4 text-blue-600 rounded"
                      />
                    </div>
                    <div className="flex items-center space-x-1">
                      <button
                        onClick={(e) => toggleStar(file.id, e)}
                        className={`p-1 rounded hover:bg-gray-100 ${file.starred ? 'text-yellow-500' : 'text-gray-400'}`}
                      >
                        <Star size={16} fill={file.starred ? 'currentColor' : 'none'} />
                      </button>
                      <button className="p-1 rounded hover:bg-gray-100 text-gray-400">
                        <MoreVertical size={16} />
                      </button>
                    </div>
                  </div>
                  
                  <div className="space-y-1">
                    <h3 className="font-medium text-gray-800 truncate" title={file.name}>
                      {file.name}
                    </h3>
                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <span>{formatDate(file.modified)}</span>
                      <span>{formatFileSize(file.size)}</span>
                    </div>
                  </div>
                  
                  <div className="flex items-center justify-between mt-3 pt-2 border-t border-gray-100">
                    <div className="flex items-center space-x-2">
                      {file.shared && <Share2 size={14} className="text-blue-500" />}
                      <span className="text-xs text-gray-500">Owner: {file.owner}</span>
                    </div>
                    {file.type !== 'folder' && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          console.log('Download:', file.name);
                        }}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        <Download size={14} />
                      </button>
                    )}
                  </div>
                </div>
              ) : (
                <div
                  onClick={() => handleFileClick(file)}
                  className={`flex items-center p-3 rounded-lg cursor-pointer transition-all hover:bg-gray-50 ${
                    selectedFiles.has(file.id) ? 'bg-blue-50 border-l-4 border-blue-500' : ''
                  }`}
                >
                  <div className="flex items-center space-x-3 flex-1">
                    <input
                      type="checkbox"
                      checked={selectedFiles.has(file.id)}
                      onChange={(e) => handleFileSelect(file.id, e)}
                      className="w-4 h-4 text-blue-600 rounded"
                    />
                    {getFileIcon(file.type)}
                    <div className="flex-1 min-w-0">
                      <h3 className="font-medium text-gray-800 truncate">{file.name}</h3>
                    </div>
                  </div>
                  
                  <div className="flex items-center space-x-6 text-sm text-gray-500">
                    <span className="w-20 text-right">{formatFileSize(file.size)}</span>
                    <span className="w-24 text-right">{formatDate(file.modified)}</span>
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={(e) => toggleStar(file.id, e)}
                        className={`p-1 rounded hover:bg-gray-100 ${file.starred ? 'text-yellow-500' : 'text-gray-400'}`}
                      >
                        <Star size={16} fill={file.starred ? 'currentColor' : 'none'} />
                      </button>
                      {file.shared && <Share2 size={16} className="text-blue-500" />}
                      <button className="p-1 rounded hover:bg-gray-100 text-gray-400">
                        <MoreVertical size={16} />
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Selection Actions */}
      {selectedFiles.size > 0 && (
        <div className="fixed bottom-6 left-1/2 transform -translate-x-1/2 bg-white rounded-lg shadow-lg border border-gray-200 px-4 py-3">
          <div className="flex items-center space-x-4">
            <span className="text-sm font-medium text-gray-700">
              {selectedFiles.size} selected
            </span>
            <div className="flex items-center space-x-2">
              <button className="flex items-center space-x-1 px-3 py-1 text-sm text-gray-600 hover:bg-gray-100 rounded">
                <Download size={16} />
                <span>Download</span>
              </button>
              <button className="flex items-center space-x-1 px-3 py-1 text-sm text-gray-600 hover:bg-gray-100 rounded">
                <Share2 size={16} />
                <span>Share</span>
              </button>
              <button className="flex items-center space-x-1 px-3 py-1 text-sm text-red-600 hover:bg-red-50 rounded">
                <Trash2 size={16} />
                <span>Delete</span>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GoogleDriveFiles;
