import React, { useState, useEffect } from 'react';
import { Folder, File, ChevronLeft, Download, Loader2, AlertCircle, Eye, FileText, Image, Video, Archive, MoreVertical } from 'lucide-react';
import { formatFileSize, formatDate } from '../../utils/formatters';
import apiService from '../../services/api';
import FileUpload from '../FileUpload/FileUpload';
import DownloadProgress from '../DownloadProgress/DownloadProgress';
import './FileExplorer.css';

const FileExplorer = ({ onFileSelect }) => {
  const [currentPath, setCurrentPath] = useState('');
  const [files, setFiles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [breadcrumbs, setBreadcrumbs] = useState([]);
  const [uploadSuccess, setUploadSuccess] = useState(null);
  const [uploadError, setUploadError] = useState(null);
  const [downloadSessions, setDownloadSessions] = useState([]);
  const [showDownloadProgress, setShowDownloadProgress] = useState(false);
  const [clientId, setClientId] = useState(null);
  const [showAll, setShowAll] = useState(false);
  const [displayLimit, setDisplayLimit] = useState(10);

  // Get client ID for downloads
  useEffect(() => {
    const getClientId = async () => {
      try {
        const response = await apiService.connect();
        setClientId(response.client_id);
        console.log('Client connected:', response.client_id);
      } catch (error) {
        console.error('Failed to get client ID:', error);
        setError(`Connection failed: ${error.message}`);
      }
    };
    getClientId();
  }, []);

  useEffect(() => {
    fetchFiles(currentPath);
  }, [currentPath]);

  const fetchFiles = async (path) => {
    try {
      setIsLoading(true);
      setError(null);
      
      const data = await apiService.listFiles(path);
      setFiles(data.files);
      
      // Generate breadcrumbs
      const pathParts = path.split('/').filter(Boolean);
      const crumbs = [];
      let accumulatedPath = '';
      
      for (let i = 0; i < pathParts.length; i++) {
        accumulatedPath += (i > 0 ? '/' : '') + pathParts[i];
        crumbs.push({
          name: pathParts[i],
          path: accumulatedPath
        });
      }
      
      setBreadcrumbs(crumbs);
    } catch (err) {
      console.error('Failed to fetch files:', err);
      setError(err.message);
      setFiles([]);
    } finally {
      setIsLoading(false);
    }
  };

  const navigateToDirectory = (path) => {
    setCurrentPath(path);
  };

  const navigateUp = () => {
    if (!currentPath) return;
    
    const pathParts = currentPath.split('/');
    pathParts.pop();
    setCurrentPath(pathParts.join('/'));
  };

  const handleFileClick = (file) => {
    if (file.is_directory) {
      navigateToDirectory(file.path);
    } else {
      if (onFileSelect) {
        onFileSelect(file);
      }
    }
  };

  const getFileIcon = (fileName, isDirectory) => {
    if (isDirectory) {
      return <Folder size={16} className="text-blue-500" />;
    }
    
    const ext = fileName.split('.').pop().toLowerCase();
    
    switch (ext) {
      case 'pdf':
        return <FileText size={16} className="text-red-500" />;
      case 'pptx':
      case 'ppt':
        return <FileText size={16} className="text-orange-500" />;
      case 'xlsx':
      case 'xls':
        return <FileText size={16} className="text-green-500" />;
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return <Image size={16} className="text-blue-500" />;
      case 'mp4':
      case 'avi':
      case 'mov':
        return <Video size={16} className="text-purple-500" />;
      default:
        return <File size={16} className="text-gray-500" />;
    }
  };

  const startMegaStyleDownload = async (filePath, fileName, clientId) => {
    try {
      // Start download session using API service
      const sessionData = await apiService.startDownloadSession(filePath, clientId);
      
      // Add to downloads list
      const newDownload = {
        sessionId: sessionData.session_id,
        fileName: sessionData.file_name,
        fileSize: sessionData.file_size,
        clientId: sessionData.client_id,
        progress: 0,
        speed: 0,
        eta: 0,
        status: 'starting',
        startTime: Date.now()
      };
      setDownloadSessions(prev => [...prev, newDownload]);
      // Immediately trigger browser download (no waiting for processing)
      triggerBrowserDownload(newDownload.sessionId);
      
      // Start progress tracking
      trackDownloadProgress(newDownload.sessionId);
    } catch (error) {
      console.error('Download error:', error);
      throw error;
    }
  };

  const trackDownloadProgress = async (sessionId) => {
    const pollProgress = async () => {
      try {
        const progress = await apiService.getDownloadProgress(sessionId);
        
        setDownloadSessions(prev => prev.map(download => 
          download.sessionId === sessionId 
            ? {
              ...download,
              progress: progress.progress_percent,
              speed: progress.speed_mbps,
              eta: progress.eta_seconds,
              bytesTransferred: progress.bytes_transferred,
              status: progress.is_complete ? 'completed' : 
                     progress.is_processing ? 'downloading' : 'starting'
            }
            : download
        ));
        // Continue polling while download is active
        if (!progress.error && !progress.is_complete) {
          setTimeout(pollProgress, 500);  // Poll every 500ms for smoother updates
        } else if (progress.is_complete) {
          // Mark as completed - no need to trigger another download
          setDownloadSessions(prev => prev.map(download => 
            download.sessionId === sessionId 
              ? { ...download, status: 'completed' }
              : download
          ));
        }
      } catch (error) {
        console.error('Progress tracking error:', error);
        setDownloadSessions(prev => prev.map(download => 
          download.sessionId === sessionId 
            ? { ...download, status: 'error', error: error.message }
            : download
        ));
      }
    };
    pollProgress();
  };

  const triggerBrowserDownload = (sessionId) => {
    // Update status to show download is starting
    setDownloadSessions(prev => prev.map(download => 
      download.sessionId === sessionId 
        ? { ...download, status: 'downloading' }
        : download
    ));
    // Create hidden link to trigger download - use full URL from API service
    const link = document.createElement('a');
    const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    link.href = `${baseURL}/transfer/stream-download/${sessionId}`;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();
    // Clean up
    setTimeout(() => {
      document.body.removeChild(link);
    }, 100);
  };

  const handleDownload = async (file, e) => {
    e.stopPropagation();
    
    // Try to reconnect if no client ID
    if (!clientId) {
      try {
        console.log('No client ID, attempting to reconnect...');
        const response = await apiService.connect();
        setClientId(response.client_id);
        console.log('Reconnected with client ID:', response.client_id);
      } catch (error) {
        console.error('Reconnection failed:', error);
        setError(`Connection failed: ${error.message}`);
        return;
      }
    }
    try {
      // Show download progress panel
      setShowDownloadProgress(true);
      
      // Start the Mega.nz style download directly
      await startMegaStyleDownload(file.path, file.name, clientId);
    } catch (err) {
      console.error('Download failed:', err);
      setError(err.message);
    }
  };

  const handlePreview = (file, e) => {
    e.stopPropagation();
    // Add preview functionality here
    console.log('Preview file:', file.name);
  };

  const handleUploadSuccess = (response) => {
    setUploadSuccess(`Successfully uploaded ${response.filename}`);
    setUploadError(null);
    fetchFiles(currentPath); // Refresh file list
    
    // Clear success message after 5 seconds
    setTimeout(() => {
      setUploadSuccess(null);
    }, 5000);
  };

  const handleUploadError = (error) => {
    setUploadError(error);
    setUploadSuccess(null);
    
    // Clear error message after 7 seconds
    setTimeout(() => {
      setUploadError(null);
    }, 7000);
  };

  const toggleShowAll = () => {
    setShowAll(!showAll);
  };

  const displayedFiles = showAll ? files : files.slice(0, displayLimit);

  if (error) {
    return (
      <div className="file-explorer error">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <h3 className="text-xl font-semibold mb-2">Error Loading Files</h3>
        <p className="text-gray-600 mb-4">{error}</p>
        <button 
          onClick={() => fetchFiles(currentPath)}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          Retry
        </button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="file-explorer loading">
        <Loader2 size={48} className="animate-spin text-blue-500 mb-4" />
        <p>Loading files...</p>
      </div>
    );
  }

  return (
    <div className="file-explorer">
      <div className="file-explorer-header">
        <div className="flex items-center justify-between mb-2">
          <button
            onClick={navigateUp}
            disabled={!currentPath}
            className={`back-button ${!currentPath ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            <ChevronLeft size={16} />
            Up
          </button>
          
          <div className="flex-1 ml-4">
            <div className="breadcrumbs flex items-center overflow-x-auto py-1">
              <button
                onClick={() => navigateToDirectory('')}
                className="text-blue-500 hover:text-blue-700 text-sm font-medium"
              >
                Root
              </button>
              
              {breadcrumbs.map((crumb, index) => (
                <React.Fragment key={index}>
                  <span className="mx-2 text-gray-400">/</span>
                  <button
                    onClick={() => navigateToDirectory(crumb.path)}
                    className="text-blue-500 hover:text-blue-700 text-sm font-medium whitespace-nowrap"
                  >
                    {crumb.name}
                  </button>
                </React.Fragment>
              ))}
            </div>
          </div>
        </div>
        
        <div className="path text-sm text-gray-500 truncate">
          {currentPath || 'Root directory'}
        </div>
      </div>
      
      {files.length === 0 ? (
        <div className="empty-directory">
          <Folder size={48} className="mx-auto mb-4 text-gray-400" />
          <h3 className="text-lg font-medium text-gray-600">Empty directory</h3>
          <p className="text-gray-500">No files found in this location</p>
        </div>
      ) : (
        <div className="files-container">
          {/* Files Header */}
          <div className="files-header">
            <h3 className="text-sm font-medium text-gray-700 mb-4">
              Recent Files ({files.length})
            </h3>
          </div>

          {/* Files List */}
          <div className="files-list">
            {displayedFiles.map((file) => (
              <div
                key={file.path}
                className="file-row group hover:bg-gray-50 border-b border-gray-100 last:border-b-0"
              >
                <div className="file-row-content">
                  {/* File Icon and Name */}
                  <div className="file-main-info">
                    <div className="file-icon-container">
                      {getFileIcon(file.name, file.is_directory)}
                    </div>
                    <div className="file-name-container">
                      <div 
                        className="file-name cursor-pointer hover:text-blue-600"
                        onClick={() => handleFileClick(file)}
                      >
                        {file.name}
                      </div>
                      <div className="file-size text-xs text-gray-500">
                        {formatFileSize(file.size)}
                      </div>
                    </div>
                  </div>

                  {/* File Date */}
                  <div className="file-date text-xs text-gray-500">
                    {formatDate(file.modified_time)}
                  </div>

                  {/* Actions */}
                  <div className="file-actions">
                    {!file.is_directory && (
                      <>
                        <button
                          onClick={(e) => handlePreview(file, e)}
                          className="action-btn preview-btn"
                          title="Preview"
                        >
                          <Eye size={14} />
                          <span>Preview</span>
                        </button>
                        <button
                          onClick={(e) => handleDownload(file, e)}
                          className="action-btn download-btn"
                          title="Download"
                        >
                          <Download size={14} />
                          <span>Download</span>
                        </button>
                      </>
                    )}
                    <button className="action-btn more-btn">
                      <MoreVertical size={14} />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Show More/Less Button */}
          {files.length > displayLimit && (
            <div className="show-more-container">
              <button
                onClick={toggleShowAll}
                className="show-more-btn"
              >
                {showAll ? `Show Less Files` : `Show All (${files.length - displayLimit} more)`}
              </button>
            </div>
          )}
        </div>
      )}
      
      <FileUpload 
        currentPath={currentPath}
        onUploadSuccess={handleUploadSuccess}
        onUploadError={handleUploadError}
      />
      
      {uploadSuccess && (
        <div className="upload-feedback success">
          {uploadSuccess}
        </div>
      )}
      
      {uploadError && (
        <div className="upload-feedback error">
          {uploadError}
        </div>
      )}
      
      {showDownloadProgress && downloadSessions.length > 0 && (
        <DownloadProgress 
          downloads={downloadSessions}
          onClose={() => setShowDownloadProgress(false)}
          onRemoveDownload={(sessionId) => 
            setDownloadSessions(prev => prev.filter(d => d.sessionId !== sessionId))
          }
        />
      )}

      <style jsx>{`
        .files-container {
          background: white;
          border-radius: 8px;
          border: 1px solid #e5e7eb;
          margin-top: 1rem;
        }

        .files-header {
          padding: 1rem 1.5rem 0;
          border-bottom: 1px solid #f3f4f6;
        }

        .files-list {
          padding: 0;
        }

        .file-row {
          padding: 0.75rem 1.5rem;
          transition: background-color 0.15s ease;
        }

        .file-row:hover {
          background-color: #f9fafb;
        }

        .file-row-content {
          display: flex;
          align-items: center;
          justify-content: space-between;
          width: 100%;
        }

        .file-main-info {
          display: flex;
          align-items: center;
          flex: 1;
          min-width: 0;
        }

        .file-icon-container {
          margin-right: 0.75rem;
          flex-shrink: 0;
        }

        .file-name-container {
          flex: 1;
          min-width: 0;
        }

        .file-name {
          font-size: 0.875rem;
          font-weight: 500;
          color: #111827;
          margin-bottom: 0.25rem;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }

        .file-name:hover {
          color: #2563eb;
        }

        .file-size {
          font-size: 0.75rem;
          color: #6b7280;
        }

        .file-date {
          font-size: 0.75rem;
          color: #6b7280;
          margin-right: 1rem;
          flex-shrink: 0;
        }

        .file-actions {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          opacity: 0;
          transition: opacity 0.15s ease;
        }

        .file-row:hover .file-actions {
          opacity: 1;
        }

        .action-btn {
          display: flex;
          align-items: center;
          gap: 0.25rem;
          padding: 0.25rem 0.5rem;
          border: 1px solid #d1d5db;
          background: white;
          border-radius: 4px;
          font-size: 0.75rem;
          color: #6b7280;
          cursor: pointer;
          transition: all 0.15s ease;
        }

        .action-btn:hover {
          background: #f3f4f6;
          border-color: #9ca3af;
        }

        .download-btn {
          background: #10b981;
          color: white;
          border-color: #10b981;
        }

        .download-btn:hover {
          background: #059669;
          border-color: #059669;
        }

        .preview-btn:hover {
          background: #dbeafe;
          border-color: #3b82f6;
          color: #3b82f6;
        }

        .more-btn {
          padding: 0.25rem;
          border: none;
          background: none;
        }

        .more-btn:hover {
          background: #f3f4f6;
          border-radius: 4px;
        }

        .show-more-container {
          padding: 1rem;
          text-align: center;
          border-top: 1px solid #f3f4f6;
        }

        .show-more-btn {
          color: #3b82f6;
          font-size: 0.875rem;
          font-weight: 500;
          cursor: pointer;
          padding: 0.5rem 1rem;
          border: none;
          background: none;
          border-radius: 4px;
          transition: all 0.15s ease;
        }

        .show-more-btn:hover {
          background: #dbeafe;
          color: #1d4ed8;
        }

        @media (max-width: 768px) {
          .file-row-content {
            flex-direction: column;
            align-items: flex-start;
            gap: 0.5rem;
          }

          .file-date {
            margin-right: 0;
            margin-bottom: 0.5rem;
          }

          .file-actions {
            opacity: 1;
            width: 100%;
            justify-content: flex-end;
          }
        }
      `}</style>
    </div>
  );
};

export default FileExplorer;
