 import React, { useState, useEffect } from 'react';

const AlgorithmInsights = ({ globalMetrics }) => {
    const [algorithmStats, setAlgorithmStats] = useState({});
    const [networkConditions, setNetworkConditions] = useState([]);
    const [selectedAlgorithm, setSelectedAlgorithm] = useState(null);
    const [animationTrigger, setAnimationTrigger] = useState(false);

    useEffect(() => {
        // Process metrics to show algorithm performance
        if (globalMetrics && globalMetrics.clients) {
            const stats = {};
            const conditions = [];
            
            Object.values(globalMetrics.clients).forEach(client => {
                const algorithm = client.algorithm || 'unknown';
                if (!stats[algorithm]) {
                    stats[algorithm] = {
                        count: 0,
                        avgRtt: 0,
                        avgBandwidth: 0,
                        packetLoss: 0,
                        condition: 'unknown'
                    };
                }
                
                stats[algorithm].count++;
                stats[algorithm].avgRtt += client.rtt || 0;
                stats[algorithm].avgBandwidth += client.bandwidth || 0;
                stats[algorithm].packetLoss += client.packet_loss || 0;
                
                if (client.network_condition) {
                    conditions.push({
                        algorithm,
                        condition: client.network_condition,
                        rtt: client.rtt,
                        bandwidth: client.bandwidth,
                        loss: client.packet_loss,
                        clientId: client.client_id
                    });
                }
            });
            
            // Calculate averages
            Object.keys(stats).forEach(algo => {
                const count = stats[algo].count;
                if (count > 0) {
                    stats[algo].avgRtt /= count;
                    stats[algo].avgBandwidth /= count;
                    stats[algo].packetLoss /= count;
                }
            });
            
            setAlgorithmStats(stats);
            setNetworkConditions(conditions);
            setAnimationTrigger(true);
        }
    }, [globalMetrics]);

    const getAlgorithmInfo = (algorithm) => {
        const info = {
            'reno': {
                name: 'TCP Reno',
                icon: 'üîÑ',
                color: 'from-blue-500 to-cyan-400',
                borderColor: 'border-blue-500/30',
                glowColor: 'shadow-blue-500/20',
                description: 'Fast recovery, moderate congestion handling',
                bestFor: 'General purpose, moderate congestion',
                research: 'Good balance between performance and fairness'
            },
            'cubic': {
                name: 'TCP CUBIC',
                icon: 'üìà',
                color: 'from-emerald-500 to-teal-400',
                borderColor: 'border-emerald-500/30',
                glowColor: 'shadow-emerald-500/20',
                description: 'High-speed, long-distance optimization',
                bestFor: 'High bandwidth, low loss networks',
                research: 'Default in Linux, optimized for modern networks'
            },
            'tahoe': {
                name: 'TCP Tahoe',
                icon: 'üõ°Ô∏è',
                color: 'from-orange-500 to-amber-400',
                borderColor: 'border-orange-500/30',
                glowColor: 'shadow-orange-500/20',
                description: 'Conservative, stable congestion control',
                bestFor: 'High congestion, frequent packet loss',
                research: 'Most stable under heavy congestion'
            },
            'bbr': {
                name: 'TCP BBR',
                icon: 'üöÄ',
                color: 'from-purple-500 to-pink-400',
                borderColor: 'border-purple-500/30',
                glowColor: 'shadow-purple-500/20',
                description: 'Bandwidth-based, loss-resistant',
                bestFor: 'Wireless, lossy networks',
                research: 'Maintains throughput under random loss'
            }
        };
        return info[algorithm] || {
            name: algorithm.toUpperCase(),
            icon: '‚ùì',
            color: 'from-gray-500 to-slate-400',
            borderColor: 'border-gray-500/30',
            glowColor: 'shadow-gray-500/20',
            description: 'Unknown algorithm',
            bestFor: 'Unknown',
            research: 'No data available'
        };
    };

    const getConditionBadge = (condition) => {
        const badges = {
            'excellent': { text: 'Excellent', color: 'from-emerald-500 to-green-400', icon: '‚ú®' },
            'good': { text: 'Good', color: 'from-blue-500 to-cyan-400', icon: 'üëç' },
            'congested': { text: 'Congested', color: 'from-orange-500 to-amber-400', icon: '‚ö†Ô∏è' },
            'lossy': { text: 'Lossy', color: 'from-red-500 to-pink-400', icon: 'üì°' },
            'high_bw': { text: 'High BW', color: 'from-purple-500 to-indigo-400', icon: 'üèéÔ∏è' }
        };
        return badges[condition] || { text: condition, color: 'from-gray-500 to-slate-400', icon: '‚ùì' };
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-6">
            {/* Animated Background */}
            <div className="fixed inset-0 overflow-hidden pointer-events-none">
                <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
                <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-cyan-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse animation-delay-1000"></div>
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-80 h-80 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse animation-delay-2000"></div>
            </div>

            <div className="relative z-10 max-w-7xl mx-auto">
                {/* Header with Glassmorphism */}
                <div className="backdrop-blur-xl bg-white/10 border border-white/20 rounded-3xl p-8 mb-8 shadow-2xl">
                    <div className="flex items-center justify-center mb-4">
                        <div className="w-16 h-16 bg-gradient-to-br from-purple-500 to-pink-500 rounded-2xl flex items-center justify-center text-3xl animate-bounce">
                            üß†
                        </div>
                    </div>
                    <h1 className="text-4xl font-bold text-center bg-gradient-to-r from-white via-purple-200 to-cyan-200 bg-clip-text text-transparent mb-4">
                        Intelligent TCP Algorithm Selection
                    </h1>
                    <p className="text-center text-white/70 text-lg">
                        Real-time adaptive networking powered by machine learning
                    </p>
                </div>

                {/* Algorithm Grid */}
                <div className="mb-12">
                    <h2 className="text-2xl font-bold text-white mb-8 flex items-center gap-3">
                        <span className="text-3xl">üìä</span>
                        Active Algorithms
                    </h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {Object.entries(algorithmStats).map(([algorithm, stats], index) => {
                            const info = getAlgorithmInfo(algorithm);
                            return (
                                <div
                                    key={algorithm}
                                    className={`group relative backdrop-blur-xl bg-white/10 border border-white/20 rounded-2xl p-6 hover:bg-white/20 transition-all duration-500 cursor-pointer transform hover:scale-105 hover:shadow-2xl ${info.glowColor} ${animationTrigger ? 'animate-fade-in-up' : 'opacity-0'}`}
                                    style={{ animationDelay: `${index * 200}ms` }}
                                    onClick={() => setSelectedAlgorithm(selectedAlgorithm === algorithm ? null : algorithm)}
                                >
                                    {/* Gradient Border Effect */}
                                    <div className={`absolute inset-0 bg-gradient-to-r ${info.color} opacity-0 group-hover:opacity-20 rounded-2xl transition-opacity duration-500`}></div>
                                    
                                    <div className="relative z-10">
                                        <div className="flex items-center gap-4 mb-6">
                                            <div className={`w-14 h-14 bg-gradient-to-br ${info.color} rounded-xl flex items-center justify-center text-2xl transform group-hover:rotate-12 transition-transform duration-300`}>
                                                {info.icon}
                                            </div>
                                            <div>
                                                <h3 className="text-xl font-bold text-white">{info.name}</h3>
                                                <p className="text-white/60 text-sm">{stats.count} connections</p>
                                            </div>
                                        </div>

                                        <div className="space-y-4">
                                            <div className="flex justify-between items-center">
                                                <span className="text-white/70">Avg RTT</span>
                                                <span className="text-white font-semibold">{stats.avgRtt.toFixed(1)}ms</span>
                                            </div>
                                            <div className="flex justify-between items-center">
                                                <span className="text-white/70">Bandwidth</span>
                                                <span className="text-white font-semibold">{(stats.avgBandwidth / 1024 / 1024).toFixed(1)}MB/s</span>
                                            </div>
                                            <div className="flex justify-between items-center">
                                                <span className="text-white/70">Loss Rate</span>
                                                <span className="text-white font-semibold">{(stats.packetLoss * 100).toFixed(2)}%</span>
                                            </div>
                                        </div>

                                        {/* Expandable Details */}
                                        <div className={`mt-6 overflow-hidden transition-all duration-500 ${selectedAlgorithm === algorithm ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'}`}>
                                            <div className="border-t border-white/20 pt-4">
                                                <p className="text-green-400 font-medium mb-2">Best For:</p>
                                                <p className="text-white/80 text-sm mb-3">{info.bestFor}</p>
                                                <p className="text-cyan-400 font-medium mb-2">Research Notes:</p>
                                                <p className="text-white/80 text-sm italic">{info.research}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* Network Conditions */}
                <div className="mb-12">
                    <h2 className="text-2xl font-bold text-white mb-8 flex items-center gap-3">
                        <span className="text-3xl">üåê</span>
                        Network Conditions & Algorithm Switching
                    </h2>
                    <div className="space-y-4">
                        {networkConditions.map((item, index) => {
                            const badge = getConditionBadge(item.condition);
                            const info = getAlgorithmInfo(item.algorithm);
                            
                            return (
                                <div
                                    key={index}
                                    className={`backdrop-blur-xl bg-white/10 border border-white/20 rounded-2xl p-6 hover:bg-white/20 transition-all duration-300 transform hover:scale-[1.02] ${animationTrigger ? 'animate-fade-in-left' : 'opacity-0'}`}
                                    style={{ animationDelay: `${index * 100}ms` }}
                                >
                                    <div className="flex flex-wrap items-center gap-4 mb-4">
                                        <div className={`px-4 py-2 bg-gradient-to-r ${badge.color} rounded-full text-white font-medium flex items-center gap-2 shadow-lg`}>
                                            <span>{badge.icon}</span>
                                            {badge.text}
                                        </div>
                                        <div className={`px-4 py-2 bg-gradient-to-r ${info.color} rounded-full text-white font-medium flex items-center gap-2 shadow-lg`}>
                                            <span>{info.icon}</span>
                                            {info.name}
                                        </div>
                                    </div>
                                    
                                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-white">{item.rtt?.toFixed(1)}ms</div>
                                            <div className="text-white/60 text-sm">RTT</div>
                                        </div>
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-white">{((item.bandwidth || 0) / 1024 / 1024).toFixed(1)}MB/s</div>
                                            <div className="text-white/60 text-sm">Bandwidth</div>
                                        </div>
                                        <div className="text-center">
                                            <div className="text-2xl font-bold text-white">{((item.loss || 0) * 100).toFixed(2)}%</div>
                                            <div className="text-white/60 text-sm">Loss</div>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* Research Logic */}
                <div className="backdrop-blur-xl bg-white/10 border border-white/20 rounded-3xl p-8 shadow-2xl">
                    <h2 className="text-2xl font-bold text-white mb-8 flex items-center gap-3">
                        <span className="text-3xl">üî¨</span>
                        Research-Based Switching Logic
                    </h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {[
                            { condition: 'High Loss (>2%) + Low RTT', action: 'Reno (fast recovery)', color: 'from-blue-500 to-cyan-400' },
                            { condition: 'High Loss (>2%) + High RTT', action: 'Tahoe (stability)', color: 'from-orange-500 to-amber-400' },
                            { condition: 'High BDP (BW√óRTT > 1000)', action: 'CUBIC (long fat networks)', color: 'from-emerald-500 to-teal-400' },
                            { condition: 'Wireless/Random Loss (0.1%-1%)', action: 'BBR (loss tolerance)', color: 'from-purple-500 to-pink-400' },
                            { condition: 'Low Loss + Low RTT', action: 'CUBIC (high throughput)', color: 'from-emerald-500 to-teal-400' }
                        ].map((rule, index) => (
                            <div
                                key={index}
                                className={`group relative backdrop-blur-md bg-white/5 border border-white/10 rounded-2xl p-6 hover:bg-white/10 transition-all duration-300 ${animationTrigger ? 'animate-fade-in-up' : 'opacity-0'}`}
                                style={{ animationDelay: `${index * 150}ms` }}
                            >
                                <div className={`absolute inset-0 bg-gradient-to-r ${rule.color} opacity-0 group-hover:opacity-10 rounded-2xl transition-opacity duration-300`}></div>
                                <div className="relative z-10">
                                    <div className="font-semibold text-white mb-2">{rule.condition}</div>
                                    <div className="text-2xl font-bold text-center">‚Üí</div>
                                    <div className={`text-center font-medium bg-gradient-to-r ${rule.color} bg-clip-text text-transparent`}>
                                        {rule.action}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <style jsx>{`
                @keyframes fade-in-up {
                    from {
                        opacity: 0;
                        transform: translateY(30px);
                    }
                    to {
                        opacity: 1;
                        transform: translateY(0);
                    }
                }
                
                @keyframes fade-in-left {
                    from {
                        opacity: 0;
                        transform: translateX(-30px);
                    }
                    to {
                        opacity: 1;
                        transform: translateX(0);
                    }
                }
                
                .animate-fade-in-up {
                    animation: fade-in-up 0.8s ease-out forwards;
                }
                
                .animate-fade-in-left {
                    animation: fade-in-left 0.6s ease-out forwards;
                }
                
                .animation-delay-1000 {
                    animation-delay: 1s;
                }
                
                .animation-delay-2000 {
                    animation-delay: 2s;
                }
            `}</style>
        </div>
    );
};

export default AlgorithmInsights;
