import React, { useState, useEffect } from 'react';
import './DownloadProgress.css';

const DownloadProgress = ({ onClose }) => {
    const [downloads, setDownloads] = useState([]);

    const startDownload = async (filePath, fileName, clientId) => {
        try {
            // Start download session
            const response = await fetch('/api/transfer/start-download', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    path: filePath,
                    client_id: clientId
                })
            });

            if (!response.ok) {
                throw new Error('Failed to start download');
            }

            const sessionData = await response.json();
            
            // Add to downloads list
            const newDownload = {
                sessionId: sessionData.session_id,
                fileName: sessionData.file_name,
                fileSize: sessionData.file_size,
                clientId: sessionData.client_id,
                progress: 0,
                speed: 0,
                eta: 0,
                status: 'starting',
                startTime: Date.now()
            };

            setDownloads(prev => [...prev, newDownload]);

            // Start progress tracking
            trackDownloadProgress(newDownload.sessionId);

        } catch (error) {
            console.error('Download error:', error);
            alert(`Download failed: ${error.message}`);
        }
    };

    const trackDownloadProgress = async (sessionId) => {
        const pollProgress = async () => {
            try {
                const response = await fetch(`/api/transfer/download-progress/${sessionId}`);
                if (!response.ok) return;

                const progress = await response.json();
                
                setDownloads(prev => prev.map(download => 
                    download.sessionId === sessionId 
                        ? {
                            ...download,
                            progress: progress.progress_percent,
                            speed: progress.speed_mbps,
                            eta: progress.eta_seconds,
                            bytesTransferred: progress.bytes_transferred,
                            status: progress.is_complete ? 'completed' : 'downloading'
                        }
                        : download
                ));

                // If complete, trigger browser download
                if (progress.is_complete) {
                    triggerBrowserDownload(sessionId);
                } else if (!progress.error) {
                    // Continue polling
                    setTimeout(pollProgress, 500);
                }

            } catch (error) {
                console.error('Progress tracking error:', error);
                setDownloads(prev => prev.map(download => 
                    download.sessionId === sessionId 
                        ? { ...download, status: 'error', error: error.message }
                        : download
                ));
            }
        };

        pollProgress();
    };

    const triggerBrowserDownload = (sessionId) => {
        // Create hidden iframe to trigger download
        const iframe = document.createElement('iframe');
        iframe.style.display = 'none';
        iframe.src = `/api/transfer/stream-download/${sessionId}`;
        document.body.appendChild(iframe);

        // Clean up after download
        setTimeout(() => {
            document.body.removeChild(iframe);
            setDownloads(prev => prev.map(download => 
                download.sessionId === sessionId 
                    ? { ...download, status: 'saved' }
                    : download
            ));
        }, 2000);
    };

    const cancelDownload = async (sessionId) => {
        try {
            await fetch(`/api/transfer/cancel-download/${sessionId}`, {
                method: 'POST'
            });
            
            setDownloads(prev => prev.filter(download => download.sessionId !== sessionId));
        } catch (error) {
            console.error('Cancel error:', error);
        }
    };

    const formatBytes = (bytes) => {
        if (bytes === 0) return '0 B';
        const k = 1024;
        const sizes = ['B', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
    };

    const formatTime = (seconds) => {
        if (!seconds || seconds === Infinity) return '--';
        const hours = Math.floor(seconds / 3600);
        const minutes = Math.floor((seconds % 3600) / 60);
        const secs = Math.floor(seconds % 60);
        
        if (hours > 0) return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
        return `${minutes}:${secs.toString().padStart(2, '0')}`;
    };

    // Expose function globally for FileExplorer to use
    window.startMegaStyleDownload = startDownload;

    if (downloads.length === 0) return null;

    return (
        <div className="download-progress-overlay">
            <div className="download-progress-panel">
                <div className="panel-header">
                    <h3>📥 Downloads</h3>
                    <button onClick={onClose} className="close-button">×</button>
                </div>

                <div className="downloads-list">
                    {downloads.map((download) => (
                        <div key={download.sessionId} className={`download-item ${download.status}`}>
                            <div className="download-info">
                                <div className="file-name">{download.fileName}</div>
                                <div className="file-details">
                                    <span>{formatBytes(download.fileSize)}</span>
                                    {download.speed > 0 && (
                                        <>
                                            <span>•</span>
                                            <span>{download.speed.toFixed(1)} MB/s</span>
                                        </>
                                    )}
                                    {download.eta > 0 && (
                                        <>
                                            <span>•</span>
                                            <span>ETA: {formatTime(download.eta)}</span>
                                        </>
                                    )}
                                </div>
                            </div>

                            <div className="download-progress">
                                <div className="progress-bar">
                                    <div 
                                        className="progress-fill" 
                                        style={{ width: `${download.progress}%` }}
                                    ></div>
                                </div>
                                <div className="progress-text">
                                    {download.status === 'completed' && '✅ Processing...'}
                                    {download.status === 'saved' && '✅ Saved to Downloads'}
                                    {download.status === 'downloading' && `${download.progress.toFixed(1)}%`}
                                    {download.status === 'error' && '❌ Error'}
                                </div>
                            </div>

                            <div className="download-actions">
                                {download.status === 'downloading' && (
                                    <button 
                                        onClick={() => cancelDownload(download.sessionId)}
                                        className="cancel-button"
                                    >
                                        Cancel
                                    </button>
                                )}
                                {download.status === 'saved' && (
                                    <button 
                                        onClick={() => setDownloads(prev => 
                                            prev.filter(d => d.sessionId !== download.sessionId)
                                        )}
                                        className="remove-button"
                                    >
                                        Remove
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>

                <div className="panel-footer">
                    <small>Files are downloaded in your browser with real-time TCP metrics tracking</small>
                </div>
            </div>
        </div>
    );
};

export default DownloadProgress;
