import React, { useState, useEffect } from 'react';
import {
  Folder, File, ChevronLeft, Download, Loader2, AlertCircle,
  Image as ImageIcon
} from 'lucide-react';
import {
  FiSearch, FiFilter, FiLock, FiGrid, FiList
} from 'react-icons/fi';

import { formatFileSize, formatDate } from '../../utils/formatters';
import apiService from '../../services/api';
import FileUpload from '../FileUpload/FileUpload';
import DownloadProgress from '../DownloadProgress/DownloadProgress';
import './FileExplorer.css';

const FileExplorer = ({ onFileSelect }) => {
  const [currentPath, setCurrentPath] = useState('');
  const [files, setFiles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [breadcrumbs, setBreadcrumbs] = useState([]);
  const [uploadSuccess, setUploadSuccess] = useState(null);
  const [uploadError, setUploadError] = useState(null);
  const [downloadSessions, setDownloadSessions] = useState([]);
  const [showDownloadProgress, setShowDownloadProgress] = useState(false);
  const [clientId, setClientId] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [totalSize, setTotalSize] = useState(0);
  const [viewMode, setViewMode] = useState('grid');
  const [previewFile, setPreviewFile] = useState(null);

  useEffect(() => {
    const getClientId = async () => {
      try {
        const response = await apiService.connect();
        setClientId(response.client_id);
      } catch (error) {
        setError(`Connection failed: ${error.message}`);
      }
    };
    getClientId();
  }, []);

  useEffect(() => {
    fetchFiles(currentPath);
  }, [currentPath]);

  useEffect(() => {
    const fetchTotalSize = async () => {
      try {
        const data = await apiService.listFiles('');
        const sum = data.files.reduce((acc, f) => acc + (f.size || 0), 0);
        setTotalSize(sum);
      } catch {
        setTotalSize(0);
      }
    };
    fetchTotalSize();
  }, [files]);

  const fetchFiles = async (path) => {
    try {
      setIsLoading(true);
      setError(null);
      const data = await apiService.listFiles(path);
      setFiles(data.files);

      const pathParts = path.split('/').filter(Boolean);
      const crumbs = [];
      let accumulatedPath = '';
      for (let i = 0; i < pathParts.length; i++) {
        accumulatedPath += (i > 0 ? '/' : '') + pathParts[i];
        crumbs.push({ name: pathParts[i], path: accumulatedPath });
      }
      setBreadcrumbs(crumbs);
    } catch (err) {
      setError(err.message);
      setFiles([]);
    } finally {
      setIsLoading(false);
    }
  };

  const navigateToDirectory = (path) => setCurrentPath(path);
  const navigateUp = () => {
    if (!currentPath) return;
    const pathParts = currentPath.split('/');
    pathParts.pop();
    setCurrentPath(pathParts.join('/'));
  };

  const handleFileClick = (file) => {
    if (file.is_directory) navigateToDirectory(file.path);
    else if (onFileSelect) onFileSelect(file);
  };

  const handleDownload = async (file, e) => {
    e.stopPropagation();
    if (!clientId) {
      try {
        const response = await apiService.connect();
        setClientId(response.client_id);
      } catch (error) {
        setError(`Connection failed: ${error.message}`);
        return;
      }
    }

    try {
      setShowDownloadProgress(true);
      await startMegaStyleDownload(file.path, file.name, clientId);
    } catch (err) {
      setError(err.message);
    }
  };

  const startMegaStyleDownload = async (filePath, fileName, clientId) => {
    const sessionData = await apiService.startDownloadSession(filePath, clientId);
    const newDownload = {
      sessionId: sessionData.session_id,
      fileName: sessionData.file_name,
      fileSize: sessionData.file_size,
      clientId: sessionData.client_id,
      progress: 0, speed: 0, eta: 0,
      status: 'starting',
      startTime: Date.now()
    };
    setDownloadSessions(prev => [...prev, newDownload]);
    triggerBrowserDownload(newDownload.sessionId);
    trackDownloadProgress(newDownload.sessionId);
  };

  const trackDownloadProgress = async (sessionId) => {
    const poll = async () => {
      try {
        const progress = await apiService.getDownloadProgress(sessionId);
        setDownloadSessions(prev => prev.map(download =>
          download.sessionId === sessionId
            ? { ...download, ...progress, status: progress.is_complete ? 'completed' : 'downloading' }
            : download
        ));
        if (!progress.error && !progress.is_complete) {
          setTimeout(poll, 500);
        }
      } catch (error) {
        setDownloadSessions(prev => prev.map(download =>
          download.sessionId === sessionId
            ? { ...download, status: 'error', error: error.message }
            : download
        ));
      }
    };
    poll();
  };

  const triggerBrowserDownload = (sessionId) => {
    const link = document.createElement('a');
    const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    link.href = `${baseURL}/transfer/stream-download/${sessionId}`;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();
    setTimeout(() => document.body.removeChild(link), 100);
  };

  const handleUploadSuccess = (res) => {
    setUploadSuccess(`Successfully uploaded ${res.filename}`);
    setUploadError(null);
    fetchFiles(currentPath);
    setTimeout(() => setUploadSuccess(null), 5000);
  };

  const handleUploadError = (err) => {
    setUploadError(err);
    setUploadSuccess(null);
    setTimeout(() => setUploadError(null), 7000);
  };

  const handleSearchChange = async (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    if (!query.trim()) return setSearchResults(null);

    try {
      const data = await apiService.listFiles(currentPath);
      setSearchResults(data.files.filter(f =>
        f.name.toLowerCase().includes(query.toLowerCase())
      ));
    } catch {
      setSearchResults([]);
    }
  };

  const handlePreview = (file, e) => {
    e.stopPropagation();
    setPreviewFile(file);
  };

  if (error) {
    return (
      <div className="file-explorer error">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <h3>Error Loading Files</h3>
        <p>{error}</p>
        <button onClick={() => fetchFiles(currentPath)}>Retry</button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="file-explorer loading">
        <Loader2 size={48} className="animate-spin text-blue-500 mb-4" />
        <p>Loading files...</p>
      </div>
    );
  }

  const displayedFiles = searchResults ?? files;

  return (
    <div className="file-explorer">
      <div className="file-manager-header">
        <div className="left-section">
          <h2>File Manager</h2>
          <div className="search-filter-group">
            <div className="search-box">
              <FiSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search files‚Ä¶"
                value={searchQuery}
                onChange={handleSearchChange}
              />
            </div>
            <button className="filter-button">
              <FiFilter className="filter-icon" /> Filter
            </button>
          </div>
        </div>
        <div className="right-section">
          <div className="storage-indicator">
            <FiLock /> <span>{formatFileSize(totalSize)} / 10 GB</span>
          </div>
          <div className="view-toggle">
            <button onClick={() => setViewMode('grid')} className={viewMode === 'grid' ? 'selected' : ''}><FiGrid /></button>
            <button onClick={() => setViewMode('list')} className={viewMode === 'list' ? 'selected' : ''}><FiList /></button>
          </div>
        </div>
      </div>

      <div className="recent-files-label">Recent Files</div>

      {displayedFiles.length === 0 ? (
        <div className="empty-directory">
          <Folder size={48} />
          <h3>No files found</h3>
        </div>
      ) : viewMode === 'grid' ? (
        <div className="file-grid">
          {displayedFiles.map(file => (
            <div key={file.path} className="file-card" onClick={() => handleFileClick(file)}>
              <div className="file-card-preview">
                {file.is_directory ? (
                  <Folder size={56} />
                ) : file.mime_type?.startsWith('image/') ? (
                  <ImageIcon size={56} />
                ) : file.mime_type?.startsWith('video/') ? (
                  <span>üé¨</span>
                ) : (
                  <File size={56} />
                )}
              </div>
              <div className="file-card-info">
                <div className="file-card-name" title={file.name}>{file.name}</div>
                <div className="file-card-meta">
                  <span>{formatFileSize(file.size)}</span>
                  <span>{formatDate(file.modified_time)}</span>
                </div>
                {!file.is_directory && (
                  <div className="file-card-buttons">
                    <button onClick={(e) => handlePreview(file, e)} className="preview-btn">Preview</button>
                    <button onClick={(e) => handleDownload(file, e)} className="download-button">
                      <Download size={16} />
                      <span>Download</span>
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="file-list">
          {displayedFiles.map(file => (
            <div key={file.path} className="file-item" onClick={() => handleFileClick(file)}>
              <div className="file-icon">{file.is_directory ? <Folder size={24} /> : <File size={24} />}</div>
              <div className="file-info">
                <div className="file-name">{file.name}</div>
                <div className="file-details">
                  <span>{formatFileSize(file.size)}</span>
                  <span>{formatDate(file.modified_time)}</span>
                </div>
              </div>
              {!file.is_directory && (
                <div className="file-actions">
                  <button onClick={(e) => handlePreview(file, e)} className="preview-btn">Preview</button>
                  <button onClick={(e) => handleDownload(file, e)} className="download-button">Download</button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      <FileUpload
        currentPath={currentPath}
        onUploadSuccess={handleUploadSuccess}
        onUploadError={handleUploadError}
      />

      {uploadSuccess && <div className="upload-feedback success">{uploadSuccess}</div>}
      {uploadError && <div className="upload-feedback error">{uploadError}</div>}

      {showDownloadProgress && downloadSessions.length > 0 && (
        <DownloadProgress
          downloads={downloadSessions}
          onClose={() => setShowDownloadProgress(false)}
          onRemoveDownload={(sessionId) => setDownloadSessions(prev => prev.filter(d => d.sessionId !== sessionId))}
        />
      )}

      {/* üëÅÔ∏è Preview Modal */}
      {previewFile && (
        <div className="file-preview-modal" onClick={() => setPreviewFile(null)}>
          <div className="preview-content" onClick={(e) => e.stopPropagation()}>
            <h3>Preview: {previewFile.name}</h3>
            <button className="close-preview" onClick={() => setPreviewFile(null)}>‚úñ</button>
            {previewFile.mime_type?.startsWith('image/') && (
              <img
                src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/files/raw/${previewFile.path}`}
                alt={previewFile.name}
                style={{ maxWidth: '100%' }}
              />
            )}
            {previewFile.mime_type?.startsWith('video/') && (
              <video controls style={{ maxWidth: '100%' }}>
                <source
                  src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/files/raw/${previewFile.path}`}
                  type={previewFile.mime_type}
                />
              </video>
            )}
            {previewFile.mime_type === 'application/pdf' && (
              <iframe
                src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/files/raw/${previewFile.path}`}
                title="PDF Preview"
                width="100%"
                height="600px"
              />
            )}
            {!['image', 'video', 'application/pdf'].some(t => previewFile.mime_type?.startsWith(t)) && (
              <p>Preview not available for this file type.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default FileExplorer;
