import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import FileExplorer from './components/FileExplorer/FileExplorer';
import MetricsDashboard from './components/MetricsDashboard/MetricsDashboard';
import ControlPanel from './components/ControlPanel/ControlPanel';
import AlgorithmInsights from './components/AlgorithmInsights/AlgorithmInsights';
import apiService from './services/api';
import './App.css';

function Home({ handleFileSelect }) {
  const navigate = useNavigate();
  return (
    <div className="app">
      <header className="app-header">
        <h1>TCP File Transfer System</h1>
      </header>
      <main className="app-main">
        <div className="app-layout">
          <div className="root-panel">
            <FileExplorer onFileSelect={handleFileSelect} />
            <button
              className="analysis-btn"
              style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
              onClick={() => navigate('/analysis')}
            >
              Analysis
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

function Analysis({ globalMetrics, setGlobalMetrics }) {
  const navigate = useNavigate();
  return (
    <div className="app">
      <header className="app-header">
        <h1>TCP File Transfer System - Analysis</h1>
        <button
          className="back-btn"
          style={{ marginLeft: '20px', padding: '6px 16px', fontSize: '14px' }}
          onClick={() => navigate('/')}
        >
          Back
        </button>
      </header>
      <main className="app-main">
        <div className="app-layout">
          <div className="left-panel">
            <ControlPanel />
            <AlgorithmInsights globalMetrics={globalMetrics} />
          </div>
          <div className="right-panel">
            <MetricsDashboard onMetricsUpdate={setGlobalMetrics} />
          </div>
        </div>
      </main>
    </div>
  );
}

function App() {
  const [connectionStatus, setConnectionStatus] = useState('connecting'); // 'connecting', 'connected', 'disconnected'
  const [error, setError] = useState(null);
  const [globalMetrics, setGlobalMetrics] = useState(null);

  useEffect(() => {
    let retryTimeout;

    const connectToServer = async () => {
      try {
        await apiService.connect();
        setConnectionStatus('connected');
        setError(null);
      } catch (err) {
        setConnectionStatus('disconnected');
        setError(err.message);
        retryTimeout = setTimeout(connectToServer, 5000); // Retry in 5 seconds
      }
    };

    connectToServer();

    return () => {
      apiService.disconnect();
      clearTimeout(retryTimeout);
    };
  }, []);

  const handleFileSelect = (file) => {
    console.log('Selected file:', file);
    // Future: preview or transfer functionality
  };

  if (connectionStatus === 'connecting') {
    return (
      <div className="app connecting">
        <div className="loading-spinner"></div>
        <p>Connecting to server...</p>
      </div>
    );
  }

  if (connectionStatus === 'disconnected') {
    return (
      <div className="app connecting">
        <div className="error-icon">⚠️</div>
        <h2>Connection Error</h2>
        <p>{error || 'Unable to connect to the server.'}</p>
        <p>Retrying in a few seconds...</p>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<Home handleFileSelect={handleFileSelect} />}
        />
        <Route
          path="/analysis"
          element={
            <Analysis
              globalMetrics={globalMetrics}
              setGlobalMetrics={setGlobalMetrics}
            />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
